import { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const TodoApp = () => {
  const [todo, setTodo] = useState('');
  const [todolist, setTodolist] = useState([]);
  const [prolist, setProList] = useState([]);
  const [completed, setCompleted] = useState([]);
  const [updating, setUpdating] = useState(false);
  const [action, setAction] = useState('Add To Task');
  const [source, setSource] = useState('');
  const [updatedSource, setUpdatedSource] = useState('');
  const [dragging, setDragging] = useState(false);
  const clickRef = useRef({});
  const dragRef = useRef({});

  useEffect(() => {
    getTodos();
    getInProgress();
    getCompleted();

    const eventSource = new EventSource('http://localhost:5002/api/stream');

    eventSource.onmessage = (event) => {
      const change = JSON.parse(event.data);
      handleDatabaseChange(change);
    };

    return () => {
      eventSource.close();
    };
  }, []);

  const getTodos = async () => {
    try {
      const response = await axios.get('http://localhost:5002/api/todo');
      setTodolist(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const getInProgress = async () => {
    try {
      const response = await axios.get('http://localhost:5002/api/inProgress');
      setProList(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const getCompleted = async () => {
    try {
      const response = await axios.get('http://localhost:5002/api/completed');
      setCompleted(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const add = async (e) => {
    e.preventDefault();
    if (todo.trim() === '') {
      alert("Task Can't Be Blank");
      return;
    }
    try {
      await axios.post(`http://localhost:5002/api/${updatedSource}`, {
        name: todo,
      });
      setTodo('');
    } catch (error) {
      console.log(error);
    }
  };

  const updateTodo = async (e) => {
    e.preventDefault();
    const id = clickRef.current.id;
    console.log(todo, source, id);
    try {
      await axios.put(`http://localhost:5002/api/${source}/${id}`, {
        name: todo,
      });
      setTodo('');
    } catch (error) {
      console.log(error);
    }
  };

  const deleteitem = async (e) => {
    e.preventDefault();
    var id = dragging ? dragRef.current.id : clickRef.current.id;
    var source = dragging ? dragRef.current.source : clickRef.current.source;
    try {
      await axios.delete(`http://localhost:5002/api/${source}/${id}`);
      setTodo('');
      setUpdating(false);
      setAction('Add To Task');
    } catch (error) {
      console.log(error);
    }
  };

  const executeAction = (e) => {
    console.log(action);
    if (action === 'Add To Task') {
      add(e);
    } else if (action === 'Update') {
      updateTodo(e);
    }
  };

  const handleClick = (source, task, id) => {
    setSource(source);
    setTodo(task);
    setUpdating(true);
    setAction('Update');
    clickRef.current = { source, task, id };
    console.log(clickRef);
  };

  const clickToReset = () => {
    setUpdating(false);
    setAction('Add To Task');
    setTodo('');
  };

  const handleDragStart = (source, item, id) => {
    setTodo(item);
    setDragging(true);
    dragRef.current = { source, item, id };
  };

  const handleDragEnter = (destination) => {
    setUpdatedSource(destination);
  };

  const handleDrop = (e) => {
    if (dragging) {
      const { source, item, id } = dragRef.current;
      if (source !== updatedSource) {
        deleteitem(e);
        add(e);
      }
      setDragging(false);
    }
  };

  const handleDatabaseChange = (change) => {
    if (change.operationType === 'insert') {
      const newDocument = change.fullDocument;
      if (change.ns.coll === 'todos') {
        setTodolist((prev) => [...prev, newDocument]);
      } else if (change.ns.coll === 'inprogress') {
        setProList((prev) => [...prev, newDocument]);
      } else if (change.ns.coll === 'completed') {
        setCompleted((prev) => [...prev, newDocument]);
      }
    } else if (change.operationType === 'update') {
      const updatedDocument = change.fullDocument;
      if (change.ns.coll === 'todos') {
        setTodolist((prev) =>
          prev.map((item) => (item._id === updatedDocument._id ? updatedDocument : item))
        );
      } else if (change.ns.coll === 'inprogress') {
        setProList((prev) =>
          prev.map((item) => (item._id === updatedDocument._id ? updatedDocument : item))
        );
      } else if (change.ns.coll === 'completed') {
        setCompleted((prev) =>
          prev.map((item) => (item._id === updatedDocument._id ? updatedDocument : item))
        );
      }
    } else if (change.operationType === 'delete') {
      const deletedDocumentId = change.documentKey._id;
      if (change.ns.coll === 'todos') {
        setTodolist((prev) => prev.filter((item) => item._id !== deletedDocumentId));
      } else if (change.ns.coll === 'inprogress') {
        setProList((prev) => prev.filter((item) => item._id !== deletedDocumentId));
      } else if (change.ns.coll === 'completed') {
        setCompleted((prev) => prev.filter((item) => item._id !== deletedDocumentId));
      }
    }
  };

  return (
    <div className="p-10 flex flex-col h-screen text-lg items-center">
      <form className="flex sm:flex-row flex-col" onSubmit={executeAction}>
        <input
          type="text"
          placeholder="New Task"
          className="border-2 px-3 py-2 flex-shrink-1"
          value={todo}
          onChange={(e) => setTodo(e.target.value)}
        />
        <input
          type="submit"
          value={action}
          className="bg-blue-500 px-4 py-2 sm:ms-1 rounded-sm border-1 border-blue-500 sm:mt-0 mt-2"
        />
        {updating ? (
          <div>
            <button
              className="bg-blue-500 px-4 py-2 sm:ms-1 rounded-sm border-1 border-blue-500 sm:mt-0 mt-2 h-full"
              onClick={deleteitem}
            >
              Delete
            </button>
            <button
              className="bg-blue-500 px-4 py-2 sm:ms-1 rounded-sm border-1 border-blue-500 sm:mt-0 mt-2 h-full"
              onClick={clickToReset}
            >
              Cancel
            </button>
          </div>
        ) : (
          ''
        )}
      </form>

      <div className="grid grid-cols-3 gap-4 mt-6 w-full h-full overflow-hidden">
        {/* To Do */}
        <div
          className="flex flex-col items-center border-2 border-black p-4 overflow-y-scroll h-full w-full bg-gray-300"
          onDragEnter={() => handleDragEnter('todo')}
          onDragEnd={handleDrop}
        >
          <h2 className="font-semibold">To Do</h2>
          {todolist.map((task, index) => (
            <div
              key={index}
              className="bg-white p-2 rounded-sm mt-3 w-full"
              draggable
              onClick={() => handleClick('todo', task.name, task._id)}
              onDragStart={() => handleDragStart('todo', task.name, task._id)}
            >
              {task.name}
            </div>
          ))}
        </div>

        {/* In Progress */}
        <div
          className="flex flex-col items-center border-2 border-black p-4 overflow-y-scroll h-full w-full bg-gray-300"
          onDragEnter={() => handleDragEnter('inProgress')}
          onDragEnd={handleDrop}
        >
          <h2 className="font-semibold">In Progress</h2>
          {prolist.map((task, index) => (
            <div
              key={index}
              className="bg-white p-2 rounded-sm mt-3 w-full"
              draggable
              onClick={() => handleClick('inProgress', task.name, task._id)}
              onDragStart={() => handleDragStart('inProgress', task.name, task._id)}
            >
              {task.name}
            </div>
          ))}
        </div>

        {/* Completed */}
        <div
          className="flex flex-col items-center border-2 border-black p-4 overflow-y-scroll h-full w-full bg-gray-300"
          onDragEnter={() => handleDragEnter('completed')}
          onDragEnd={handleDrop}
        >
          <h2 className="font-semibold">Completed</h2>
          {completed.map((task, index) => (
            <div
              key={index}
              className="bg-white p-2 rounded-sm mt-3 w-full"
              draggable
              onClick={() => handleClick('completed', task.name, task._id)}
              onDragStart={() => handleDragStart('completed', task.name, task._id)}
            >
              {task.name}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TodoApp;
